function getQuote(){let e=new XMLHttpRequest;e.open("GET","https://type.fit/api/quotes"),e.onload=function(){let e=JSON.parse(this.response)[getRandomInt(1,1643)];document.getElementById("quote").innerHTML=e.text},e.send()}function getVavokQuote(){let e=new XMLHttpRequest;e.open("GET","/quotes/vavok-quotes/get"),e.onload=function(){let e=JSON.parse(this.response),t=e.body;null!=e.author&&(t=t+"<hr />"+e.author),document.getElementById("quote").innerHTML=t},e.send()}function getRandomInt(e,t){return e=Math.ceil(e),Math.floor(Math.random()*((t=Math.floor(t))-e+1)+e)}